                    <div class="p-4">
                        <h3 class="text-lg leading-6 font-medium text-gray-900">
                            COVID-19 Data
                        </h3>
                        <p class="mt-1 text-sm text-gray-500">
                            Use the map to select data from a specific state.
                        </p>
                        <div class="flex justify-between">
                            <p>You are viewing data for {{ $locations[$currentLocation] }}</p>
                            <div class="flex justify-end">
                                <div class="relative inline-block text-left mr-3" x-data="{ open: false }">
                                    <div>
                                        <button type="button" @click="open = !open" class="inline-flex justify-center w-full rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                            Location
                                            <!-- Heroicon name: solid/chevron-down -->
                                            <svg class="-mr-1 ml-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                                <path fill-rule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clip-rule="evenodd" />
                                            </svg>
                                        </button>
                                    </div>

                                    <!--
                                      Dropdown menu, show/hide based on menu state.

                                      Entering: "transition ease-out duration-100"
                                        From: "transform opacity-0 scale-95"
                                        To: "transform opacity-100 scale-100"
                                      Leaving: "transition ease-in duration-75"
                                        From: "transform opacity-100 scale-100"
                                        To: "transform opacity-0 scale-95"
                                    -->
                                    <div x-show="open" @click.outside="open = false" class="origin-top-right absolute right-0 mt-2 w-56 max-h-56 overflow-y-auto rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none z-10" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                                        <div class="py-1" role="none">
                                            <!-- Active: "bg-gray-100 text-gray-900", Not Active: "text-gray-700" -->
                                            @foreach($locations as $locationKey => $locationName)
                                                <button wire:click="setCurrentLocation('{{ $locationKey }}')"
                                                        @click="open = false"
                                                        @class(['block w-full text-left px-4 py-2 text-sm', 'bg-gray-100 text-gray-900' => $currentLocation == $locationKey, 'text-gray-700' => $currentLocation != $locationKey])
                                                        role="menuitem" tabindex="-1" id="location-menu-item-{{ $locationKey }}">{{$locationName}}</button>
                                            @endforeach
                                        </div>
                                    </div>
                                </div>
                                <div class="relative inline-block text-left" x-data="{ open: false }">
                                    <div>
                                        <button type="button" @click="open = !open" class="inline-flex justify-center w-full rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                            Models
                                            <!-- Heroicon name: solid/chevron-down -->
                                            <svg class="-mr-1 ml-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                                <path fill-rule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clip-rule="evenodd" />
                                            </svg>
                                        </button>
                                    </div>

                                    <!--
                                      Dropdown menu, show/hide based on menu state.

                                      Entering: "transition ease-out duration-100"
                                        From: "transform opacity-0 scale-95"
                                        To: "transform opacity-100 scale-100"
                                      Leaving: "transition ease-in duration-75"
                                        From: "transform opacity-100 scale-100"
                                        To: "transform opacity-0 scale-95"
                                    -->
                                    <div x-show="open" @click.outside="open = false" class="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none z-10" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                                        <div class="py-1" role="none">
                                            <!-- Active: "bg-gray-100 text-gray-900", Not Active: "text-gray-700" -->
                                            @foreach($availableModels as $modelKey => $modelDescription)
                                                <button wire:click="toggleSpecificModel('{{ $modelKey }}')"
                                                        @click="open = false"
                                                        @class(['block w-full text-left px-4 py-2 text-sm', 'bg-gray-100 text-gray-900' => isset($currentModels[$modelKey]), 'text-gray-700' => ! isset($currentModels[$modelKey])])
                                                        role="menuitem" tabindex="-1" id="model-menu-item-{{ $modelKey }}">{{$modelDescription}}</button>
                                            @endforeach
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div>
                        <div class="mx-auto">
                            <div class="px-4 sm:px-6 md:px-8" wire:loading>
                                Loading...
                            </div>
                            <div wire:loading.remove>
                                <div id='dataPlot'></div>
                                <div class="grid grid-cols-2 gap-2 p-4 mt-4">
                                    <div>
                                        <h4 class="text-base leading-6 font-medium text-gray-900">
                                            Cumulative Deaths
                                        </h4>
                                        <div id="weeklyCumulativeComparisonChart"></div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>

@push('modals')
    <script>
        var layout = {
            showlegend: true,
            legend: {
                orientation: 'h'
            }
        };

        var layout2 = {
            showlegend: true,
            legend: {
                x: 0.029,
                y: 10.238,
            },
            margin: {
                l: 100
            }
        };

        document.addEventListener("DOMContentLoaded", () => {
            console.log(@json($weeklyCumulativeComparisonChartData))
            Plotly.newPlot('dataPlot', @json($timeseriesChartData), layout, {responsive: true});
            Plotly.newPlot('weeklyCumulativeComparisonChart', @json($weeklyCumulativeComparisonChartData), layout2);

            window.Livewire.on('dataUpdated', data => {
                let chartsData = JSON.parse(data);
                Plotly.newPlot('dataPlot', chartsData[0], layout, {responsive: true});
                Plotly.newPlot('weeklyCumulativeComparisonChart', chartsData[1], layout2);
            })
        });

    </script>
@endpush
